function TileSheet.Load()
  print("TileSheet:Chargement des textures...")
  
  TileSheet.TileSheet = love.graphics.newImage("Images/Floor/MainTileSheet/roguelikeSheet_transparent.png")
  local nbColumns = TileSheet.TileSheet:getWidth() / TILE_WIDTH
  local nbLines = TileSheet.TileSheet:getHeight() / TILE_HEIGHT
  local id = 1
  
  TileSheet.TileTextures[0] = nil
  for l=1,nbLines do
    for c=1,nbColumns do
      TileSheet.TileTextures[id] = love.graphics.newQuad(
        (c-1)*TILE_WIDTH, (l-1)*TILE_HEIGHT,
        TILE_WIDTH, TILE_HEIGHT, 
        TileSheet.TileSheet:getWidth(), TileSheet.TileSheet:getHeight()
        )
      id = id + 1
    end
  end
  
  TileSheet.TileTypes[10] = "Grass"
  TileSheet.TileTypes[11] = "Grass"
  TileSheet.TileTypes[13] = "Sand"
  TileSheet.TileTypes[14] = "Sand"
  TileSheet.TileTypes[15] = "Sand"
  TileSheet.TileTypes[19] = "Water"
  TileSheet.TileTypes[20] = "Water"
  TileSheet.TileTypes[21] = "Water"
  TileSheet.TileTypes[37] = "Lava"
  TileSheet.TileTypes[55] = "Tree"
  TileSheet.TileTypes[58] = "Tree"
  TileSheet.TileTypes[61] = "Tree"
  TileSheet.TileTypes[68] = "Tree"
  TileSheet.TileTypes[169] = "Rock"
  TileSheet.TileTypes[129] = "Rock"

  print("Game:Chargement des textures terminées...")
end

function TileSheet.Draw()
  local c,l
  
  for l=1,MAP_HEIGHT do
    for c=1,MAP_WIDTH do
      local id = TileSheet.Data[l][c]
      local texQuad = TileSheet.TileTextures[id]
      if texQuad ~= nil then
        love.graphics.draw(TileSheet.TileSheet, texQuad, (c-1)*TILE_WIDTH, (l-1)*TILE_HEIGHT,0,2,2)
      end
    end
  end
  
  local x = love.mouse.getX()
  local y = love.mouse.getY()
  local c = math.floor(x / TILE_WIDTH) + 1
  local l = math.floor(y / TILE_HEIGHT) + 1
  if l>0 and c>0 and l <= MAP_HEIGHT and c <= MAP_WIDTH then
    local id = TileSheet.Data[l][c]
    
    love.graphics.print(
      "Ligne: "..tostring(l)..
      " Colonne: "..tostring(c)..
      " ID: "..tostring(id)..
      " ("..tostring(TileSheet.TileTypes[id])..")"
      ,1,1)
    
  end
end

return Game